DROP TABLE promotion_email;
CREATE TABLE promotion_email (id INT AUTO_INCREMENT NOT NULL, title VARCHAR(255) NOT NULL, emails LONGTEXT NOT NULL, primaryPicture VARCHAR(255) NOT NULL, secundaryPicture1 VARCHAR(255) NOT NULL, secundaryPicture2 VARCHAR(255) NOT NULL, secundaryPicture3 VARCHAR(255) NOT NULL, tercearyPicture VARCHAR(255) NOT NULL, primaryContentTitle VARCHAR(255) DEFAULT NULL, primaryContent LONGTEXT DEFAULT NULL, secundaryContentTitle VARCHAR(255) DEFAULT NULL, secundaryContent LONGTEXT DEFAULT NULL, tercearyContentTitle VARCHAR(255) DEFAULT NULL, tercearyContent LONGTEXT DEFAULT NULL, subject VARCHAR(255) DEFAULT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;