DROP TABLE promotion_email;
CREATE TABLE promotion_email (id INT AUTO_INCREMENT NOT NULL, subject VARCHAR(255) NOT NULL, updated_at DATETIME NOT NULL, emails LONGTEXT NOT NULL, primaryPicture VARCHAR(255) NOT NULL, introPicture1 VARCHAR(255) NOT NULL, introTitle1 VARCHAR(255) DEFAULT NULL, introContent1 LONGTEXT DEFAULT NULL, introLink1 VARCHAR(255) DEFAULT NULL, introPicture2 VARCHAR(255) NOT NULL, introTitle2 VARCHAR(255) DEFAULT NULL, introContent2 LONGTEXT DEFAULT NULL, introLink2 VARCHAR(255) DEFAULT NULL, introPicture3 VARCHAR(255) NOT NULL, introTitle3 VARCHAR(255) DEFAULT NULL, introContent3 LONGTEXT DEFAULT NULL, introLink3 VARCHAR(255) DEFAULT NULL, offersTitle VARCHAR(255) DEFAULT NULL, offerItem1 VARCHAR(255) DEFAULT NULL, offerItem2 VARCHAR(255) DEFAULT NULL, offerItem3 VARCHAR(255) DEFAULT NULL, promotionPicture VARCHAR(255) NOT NULL, promotionTitle VARCHAR(255) DEFAULT NULL, promotionContent LONGTEXT DEFAULT NULL, promotionLink VARCHAR(255) DEFAULT NULL, blogTitle VARCHAR(255) DEFAULT NULL, blogId1 INT DEFAULT NULL, blogId2 INT DEFAULT NULL, servicesTitle VARCHAR(255) DEFAULT NULL, servicePicture1 VARCHAR(255) NOT NULL, serviceTitle1 VARCHAR(255) DEFAULT NULL, serviceContent1 LONGTEXT DEFAULT NULL, serviceLink1 VARCHAR(255) DEFAULT NULL, servicePicture2 VARCHAR(255) NOT NULL, serviceTitle2 VARCHAR(255) DEFAULT NULL, serviceLink2 VARCHAR(255) DEFAULT NULL, serviceContent2 LONGTEXT DEFAULT NULL, footerPicture VARCHAR(255) NOT NULL, footerPictureLink VARCHAR(255) DEFAULT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;